mapper.select:
select f_gene from t_difference_gene_${sampleId}_${metadata}
WHERE f_gene IN (
    SELECT f_gene as gene
    FROM (
        select f_gene, MAX(f_log2_fold_change) as score
        from t_difference_gene_${sampleId}_${metadata}
        GROUP BY f_gene
    ) as t

        WHERE score  >=  #{log2FoldChange}

)
GROUP BY f_gene ORDER BY SUM(ABS(f_score)) DESC

    limit #{count}

select #{sampleId} as f_sample_id, f_type_value as f_cell_type, f_type_value, f_gene, f_score, f_adjusted_p_value, f_log2_fold_change, f_p_value
from t_difference_gene_${sampleId}_${metadata}
WHERE 1 = 1

    AND f_type_value = #{typeValue}


    AND f_log2_fold_change  >=  #{log2FoldChange}


    AND f_log2_fold_change  <=  #{log2FoldChange}


    AND f_adjusted_p_value * 1.0  <=  #{adjustedPValue}


    AND f_p_value * 1.0  <=  #{pValue}


    AND f_gene IN

        #{gene}


ORDER BY f_p_value * 1.0, f_score DESC

    limit #{count}

select #{sampleId} as f_sample_id, f_type_value as f_cell_type, f_gene, f_score, f_adjusted_p_value, f_log2_fold_change, f_p_value
from t_difference_gene_${sampleId}_${metadata}
WHERE 1 = 1

    AND f_type_value = #{typeValue}
